import Main from "./components/Main";
import Nav from "./components/Nav";
import Header from "./components/Header";
import Card from "./components/ui/Card";
import { useState, useEffect } from "react";

export default function App() {
  const [shown, setShown] = useState(false);
  const [dataAll, setDataAll] = useState([]);
  // State to store the current date
  const [currentDate, setCurrentDate] = useState(new Date());

  // Fetch data from the server
  useEffect(() => {
    fetchAllData(setDataAll);
  }, []);

  return (
    <>
      {/* Render Header once with all required props */}
      <Header
        shown={shown}
        setShown={setShown}
        currentDate={currentDate}
        setCurrentDate={setCurrentDate}
      />
	  <Card
            key={data.supplier_id}
            data={data}
            dataAll={dataAll}
            setDataAll={setDataAll}
            countIssuedBlanks={countIssuedBlanks}
            hasIssuedBook={hasIssuedBook}
            currentDate={currentDate}
        />
      <div className="app-main-container">
        <Main shown={shown} setShown={setShown} />
        <Nav />
      </div>
      <Footer />
    </>
  );
}
